def reverse(s):
    txt = ''
    for i in range(len(s)-1, -1, -1):
        txt += s[i]
    return txt

def reverse(s):
    txt = []
    for i in range(len(s)-1, -1, -1):
        txt.append(s[i])
    return ''.join(txt)

