def normal(start):

   term = start
   while True:
       yield term
       term = term + 1

def iterate(start, inc):
   if inc == 1:
      for item in normal(start):
          yield item
   else:
       term = start
       while True:
           yield term
           term = term + inc

#using original 'normal'

def abnormal(start, inc):
   term = start
   while True:
       yield term
       term = term + inc

def iterate (start, inc):
   if inc == 1: 
      return normal(start)
   return abnormal(start, inc)

