def grouper(iterable, n, fillvalue=None):
    "Collect data into fixed-length chunks or blocks"
    # grouper('ABCDEFG', 3, 'x') --> ABC DEF Gxx
    args = [iter(iterable)] * n
    return izip_longest(fillvalue=fillvalue, *args)

def choice(options, prompt):
    while True:
        output = input(prompt)    # Use raw_input(prompt) for Python 2.x
        if output in options:
            return output
        else:
            print("Bad option. Options: " + ", ".join(options))

>>> def reset_inv(inventory):
    if isinstance(inventory, list):
        return []
    elif isinstance(inventory, dict):
        return {}
    elif isinstance(inventory, str):
        return 'nothing in inventory'
    elif isinstance(inventory, bool):
        return False
    else:
        return 'not inventory type'


