def locatexml(pattern,exfiles=None):
    for path, dirs, files in os.walk(settings.MEDIA_ROOT):
        for filename in fnmatch.filter(files, pattern):
            if filename not in exfiles:
                yield os.path.join(path, filename)

def locatexml(pattern,exfiles=None):
    exfiles = set(exfiles)
    for path, dirs, files in os.walk(settings.MEDIA_ROOT):
        for filename in fnmatch.filter(files, pattern):
            if filename not in exfiles:
                yield os.path.join(path, filename)

