def ascii_list(a):
    result = []
    for i in a: result.append(chr(ord(i)+2))
    return result

def ascii_list(a):
    for i in a:
        if not i.isalpha():
            rot = 0
        else if i.lower() > 'x':
            rot = 2 - 26
        else:
            rot = 2
        yield chr(ord(i) + rot)

