def parse(d,key):
    print('in')
    if type(d)==type({}):
        if key in d:
            yield d[key]
        for k in d:
            for item in parse(d[k],key):
                yield item
    if type(d)==type([]):
        for i in d:
            for item in parse(i,key):
                yield item

def parse(d,key):
    print('in')
    if type(d)==type({}):
        if key in d:
            yield d[key]
        for k in d:
            yield from parse(d[k],key)
    if type(d)==type([]):
        for i in d:
            yield from parse(i,key)

def gen_subsets_special(full_set, M, seed=123456): # generate randomish M-subsets of full_set, "far apart". import random from random import sample random.seed(seed) elements = list(full_set) allix = set(range(len(elements))) takefrom = allix.copy()
def destructive_sample(n): # Remove a random n-subset from takefrom, & return it. s = set(sample(takefrom, n)) takefrom.difference_update(s) return s
while True: if len(takefrom) >= M: # Get everything from takefrom. ix = destructive_sample(M) else: # We need to take all of takefrom, and more. ix = takefrom takefrom = allix - ix ix |= destructive_sample(M - len(ix)) assert len(ix) == M yield set(elements[i] for i in ix)

