def action(value):
    if isinstance(value, int):
        return value
    elif callable(value):
        return value()
    else:
        raise ValueError("action does not work with %s objects" % type(value))

import logging import threading import Queue
logger = logging.getLogger(__name__) logging.basicConfig(level=logging.DEBUG, format='[%(asctime)s %(threadName)s] %(message)s', datefmt='%H:%M:%S') SENTINEL = object()
def worker(cond, queue): for line in iter(queue.get, SENTINEL): line = line.strip() logger.info(line) with cond: cond.notify() logger.info('notify')
def sprinkler(cond, queue, num_workers): with open("big_file") as f: for line in f: logger.info('advancing filehandle') with cond: queue.put(line) logger.info('waiting') cond.wait() for _ in range(num_workers): queue.put(SENTINEL)
num_workers = 4 cond = threading.Condition() queue = Queue.Queue() t = threading.Thread(target=sprinkler, args=[cond, queue, num_workers]) t.start()
threads = [threading.Thread(target=worker, args=[cond, queue])] for t in threads: t.start() for t in threads: t.join()

 def looper(Mt,It):
     for x in range (Mt.shape[0]):
         for y in range (Mt.shape[1]):
             if Mt [x,y] > It[x,y]:
                Mt [x,y] +=1
             elif Mt [x,y] < It[x,y]:
                Mt [x,y] -=1

nlooper = autojit(looper)

Mt = np.random.rand(500,500)
It = np.random.rand(500,500)

%timeit looper(Mt,It)
1 loops, best of 3: 531 ms per loop

%timeit Mt[Mt > It]+=1;Mt[Mt < It]-=1
100 loops, best of 3: 2.27 ms per loop

%timeit nlooper(Mt,It)
1 loops, best of 3: 308 s per loop

