import numpy as np import numpy.linalg as linalg A = np.array([[1,0,0],[0,1,0],[1,1,0]]) e_vals, e_vecs = linalg.eig(A) e_vecs = e_vecs.T
print(e_vals) # [ 0. 1. 1.] print(e_vecs) # [[ 0. 0. 1. ] # [ 0.70710678 0. 0.70710678] # [ 0. 0.70710678 0.70710678]]
for val, vec in zip(e_vals, e_vecs): assert np.allclose(np.dot(A, vec), val * vec)

