class General:
    app_dir = 'www'

    def GET(self, path):
        root = os.path.abspath(os.path.join('.', os.pardir))
        dest = '%s/%s/index.html' % (root, self.app_dir)
        if path:
            dest = '%s/%s/%s' % (root, self.app_dir, path)
        with open(dest, 'r') as f:
            return f.read()
        return ''

