roll <- function( x , n ){
  if( n == 0 )
    return( x )
  c( tail(x,n) , head(x,-n) )
}

roll(1:5,2)
#[1] 4 5 1 2 3

#  For the situation where you supply 0 [ this would be kinda silly! :) ]
roll(1:5,0)
#[1] 1 2 3 4 5

def append(self, item):
    for s in self:
        if item == s:
            print ("Item already exists in list")
        else:
            UserList.append(self, item)

def append(self, item):
    if item in self:
        print("Item already exists in list")
    else:
        UserList.append(self, item)

def append(self, item):
    if item in self:
        raise ValueError(repr(item) + " already exists in list")
    else:
        UserList.append(self, item)

