class PropagateOuter(type):
    def __init__(cls, name, bases, dct):
        type.__init__(cls, name, bases, dct)
        if "Serialiser" in dct:
            cls.Serialiser.outer = cls

class ModelBase(object):
    __metaclass__ = PropagateOuter

# Python 3 version of the above
# class ModelBase(metaclass=PropagateOuter):
#     pass

class SerialiserBase(LibrarySerialiser):
    def __init__(self, *args, **kwargs):
        print self.outer.origin
        super(SerialiserBase, self).__init__(*args, **kwargs)

class ComponentModel(ModelBase):
    origin = 'supermarket'

    class Serialiser(SerialiserBase):
        pass

ser = ComponentModel.Serialiser()

regex = re.compile(r"""
    \{START\}  # Match {START}
    (          # Match and capture the following:
     (?:       # Start of non-capturing group, used to match a single character
      (?!      # only if it's impossible to match the following:
       \{      # - a literal {
       (?:     # Inner non-capturing group, used for the following alternation:
        START  # - Either match the word START
       |       # or
        END    # - the word END
       )       # End of inner non-capturing group
       \}      # - a literal }
      )        # End of negative lookahead assertion
      .        # Match any single character
     )*        # Repeat as often as possible
    )          # End of capturing group 1
    \{END\}    # until {END} is matched.""", 
    re.VERBOSE)

