class MyDialog(QtGui.QDialog): def __init__(self,parent=None): QtGui.QDialog.__init__(self,parent) layout = QtGui.QVBoxLayout() self.msg = QtGui.QLabel('some sort of status') self.buttonbox = QtGui.QDialogButtonBox(QtGui.QDialogButtonBox.Ok, QtCore.Qt.Horizontal, self) self.buttonbox.accepted.connect(self.accept) layout.addWidget(self.msg) layout.addWidget(self.buttonbox) self.setLayout(layout)
def set_msg(self, new_msg): self.msg.setText(new_msg)
def set_function_on_accept(self,fcn): self.function = fcn
def accept(self): self.function()
class Command(object): def do(self): self.setupUi()
def do_work(self): self.pre() self.run() self.post()
def pre(self): # do setup stuff for run method time.sleep(1) self.diag.set_msg("stuff setup") QtGui.QApplication.processEvents()
def run(self): # do actual work time.sleep(1) self.diag.set_msg("work done") QtGui.QApplication.processEvents()
def post(self): # clean up after run time.sleep(1) self.diag.set_msg("cleaned up") QtGui.QApplication.processEvents()
def setupUi(self): # create a ui for this command diag = MyDialog() self.diag = diag diag.set_function_on_accept(self.do_work) diag.show()
if __name__ == "__main__": app = QtGui.QApplication(sys.argv) command = Command() command.do() sys.exit(app.exec_())

