    farm[-1].append(val)
    return val

def reap(val):
    return val, farm.pop()

farm = []

def sow(val):
    if farm:
        farm[-1].append(val)
    return val

def reap(expr):
    farm.append([])
    val = expr()
    return val, farm.pop()

