def find_actions(self, subparsers, actions_module, args): for attr in (action for action in dir(actions_module) if not action.startswith('_')): callback = getattr(actions_module, attr) desc = callback.__doc__ or '' subparser = subparsers.add_parser(attr, description=desc, help=desc)
# This add_argument describes the positional argument, like so: # 'files' -- the result of matching this argument will be # stored in `options.files` # metavar='FILE' -- The help messages will use the word "FILE" # when describing this argument # nargs='+' -- `narg` describes how many arguments this should # match. The value `+` means "all of them, but at # least one." subparser.add_argument('files', metavar='FILE', nargs='+')
def main(self, args): # setup parser parser = argparse.ArgumentParser() subparsers = parser.add_subparsers(dest='command') a = ExecuteShell() subcommand_parser = self.find_actions(subparsers, a, args)
# Run parser args = parser.parse_args(args)
# Run selected command # If we reach this statement, then: # args.command is the command the user chose, # for example, 'create' or 'list' # args.files is the list of arguments that followed 'create' or 'list' # a is an instance of ExecuteShell # getattr(a, args.command) is the attribute or method of `a` # the name in `args.command`, for example, a.create or a.list # getattr(a, args.command)(args.files) invokes that method, # passing in the list of user arguments # So, this line in effect might call `a.create(['file1', 'file2'])` getattr(a, args.command)(args.files)
if __name__ == "__main__":
a = TestShell() a.main(sys.argv[1:])

