def find_matches(key_mask, text):
    mapping = {}
    for key, value in zip(key_mask.split('/'), text.split('/')):
        if key[0] == '{' and key[-1] == '}':
            mapping[key[1:-1]] = value
        elif key != value:
            return
    return mapping

def find_matches(key_mask, text, regexes={}):
    mapping = {}
    for key, value in zip(key_mask.split('/'), text.split('/')):
        if key[0] == '{' and key[-1] == '}':
            key=key[1:-1]
            if key in regexes and not re.match(regexes[key], value):
                return
            mapping[key] = value
        elif key != value:
            return
    return mapping

