def spot_taken(i, ship):
    start = i
    end = i + len(ship)
    for ch in ocean[start:end]:
        if ch != 'O':
            return True
    return False

def spot_taken(i, ship):
    start = i
    end = i + len(ship)
    return any(ch != 'O' for ch in ocean[start:end])

