def nested_linked(l):
    d = {a: b for a,b in l}
    rv = []
    while d:
        k = [k for k in d.keys() if k not in d.values()][0]
        rrv = [ k ]
        while k in d:
            nk = d[k]
            del d[k]
            k = nk
            rrv.append( nk )
       rv.append( rrv )
   return rv

