class Block(object): sprite = pygame.image.load("dirt.png").convert_alpha() def __init__(self, x, y): self.rect = self.sprite.get_rect(centery=y, centerx=x)
class Player(object): sprite = pygame.image.load("dirt.png").convert() sprite.set_colorkey((0,255,0)) def __init__(self, x, y): self.rect = self.sprite.get_rect(centery=y, centerx=x) # indicates that we are standing on the ground # and thus are "allowed" to jump self.on_ground = True self.xvel = 0 self.yvel = 0 self.jump_speed = 10 self.move_speed = 8
def update(self, move, blocks):
# check if we can jump if move.up and self.on_ground: self.yvel -= self.jump_speed
# simple left/right movement if move.left: self.xvel = -self.move_speed if move.right: self.xvel = self.move_speed
# if in the air, fall down if not self.on_ground: self.yvel += 0.3 # but not too fast if self.yvel > max_gravity: self.yvel = max_gravity
# if no left/right movement, x speed is 0, of course if not (move.left or move.right): self.xvel = 0
# move horizontal, and check for horizontal collisions self.rect.left += self.xvel self.collide(self.xvel, 0, blocks)
# move vertically, and check for vertical collisions self.rect.top += self.yvel self.on_ground = False; self.collide(0, self.yvel, blocks)
