def pathlength(x, y):
    total_distance = 0
    for xi, xi1, yi, yi1 in zip(x[1:], x[:-1], y[1:], y[:-1]):
        leg_distance = ((xi-xi1)**2+(yi-yi1)**2)**.5
        total_distance = total_distance + leg_distance
    return total_distance

def pathlength(x,y):
    return sum(
        ((xi-xi1)**2+(yi-yi1)**2)**.5 
        for (xi, xi1, yi, yi1) in zip(x[1:], x[:-1], y[1:], y[:-1]))

import Queue

def leaves(tree):
    result = []
    queue = Queue.Queue()
    queue.put(tree)
    while not queue.empty():
        node = queue.get()
        if type(node[1]) == tuple:
            for subnode in node[1:]:
                queue.put(subnode)
        else:
            result.append(node[1])
    return result

def makeList(tree):
    if len(tree) == 2:
        return [tree[1]]

    left = tree[1]
    right = tree[2]
    return [leaves(left), leaves(right)]

