def compare(this, that, encoding="utf-8"):
    // convert this to str
    if isinstance(this, bytes):
        this = str(this, encoding)
    // convert that to str
    if isinstance(that, bytes):
        that = str(that, encoding)
    return this == that

import re

def parse(s):
    if 'day' in s:
        m = re.match(r'(?P<days>[-\d]+) day[s]*, (?P<hours>\d+):(?P<minutes>\d+):(?P<seconds>\d[\.\d+]*)', s)
    else:
        m = re.match(r'(?P<hours>\d+):(?P<minutes>\d+):(?P<seconds>\d[\.\d+]*)', s)
    return {key: float(val) for key, val in m.groupdict().iteritems()}

