def validate_input(val, min_v=100, max_v=500):
    try:
        val = float(val)
    except ValueError:
        print("Not a valid entry")
    else:
        if not min_v < val <= max_v:
            print("Value should be between {} and {}".format(min_v, max_v))
        else:
            return val
    return False

