def build_index(path): index = {} for key, pos, data in parse_fastq(path): if key not in index: # Don't overwrite duplicates- use first occurrence. index[key] = pos
return index
def error(s): sys.stderr.write(s + "\n")
def extract_key(s): # This much is fairly constant: assert(s.startswith('@')) (machine_name, rest) = s.split(':', 1) # Per wikipedia, this changes in different variants of FASTQ format: (key, rest) = rest.split('/', 1) return key
def parse_fastq(path): """ Parse the 4-line FASTQ groups in path. Validate the contents, somewhat. """ f = open(path) i = 0 # Note: iterating a file is incompatible with fh.tell(). Fake it. pos = offset = 0 for line in f: offset += len(line) lx = i % 4 i += 1 if lx == 0: # @machine: key key = extract_key(line) len1 = len2 = 0 data = [ line ] elif lx == 1: data.append(line) len1 = len(line) elif lx == 2: # +machine: key or something assert(line.startswith('+')) data.append(line) else: # lx == 3 : quality data data.append(line) len2 = len(line)
if len2 != len1: error("Data length mismatch at line " + str(i-2) + " (len: " + str(len1) + ") and line " + str(i) + " (len: " + str(len2) + ")\n") #print "Yielding @%i: %s" % (pos, key) yield key, pos, data pos = offset
if i % 4 != 0: error("EOF encountered in mid-record at line " + str(i));
def match_records(path, index): results = [] for key, pos, d in parse_fastq(path): if key in index: # found a match! results.append(key)
return results
def write_matches(inpath, matches, outpath): rf = open(inpath) wf = open(outpath, 'w')
for m in matches: rf.seek(m) wf.write(rf.readline()) wf.write(rf.readline()) wf.write(rf.readline()) wf.write(rf.readline())
rf.close() wf.close()
#import pdb; pdb.set_trace() index = build_index('afile.fastq') matches = match_records('bfile.fastq', index) posns = [ index[k] for k in matches ] write_matches('afile.fastq', posns, 'outfile.fastq')

