class counted(object):
    """ counts how often a function is called """
    def __init__(self, func):
        self.func = func
        self.counter = 0

    def __call__(self, *args, **kwargs):
        self.counter += 1
        return self.func(*args, **kwargs)


@counted
def something():
    pass

something()
print something.counter

#!/usr/bin/env python import re
def lreplace(s, old, new): """Return a copy of string `s` with leading occurrences of substring `old` replaced by `new`.
>>> lreplace('abcabcdefabc', 'abc', 'X') 'XXdefabc' >>> lreplace('_abc', 'abc', 'X') '_abc' """ return re.sub(r'^(?:%s)+' % re.escape(old), lambda m: new * (m.end() / len(old)), s)

