class Cell(object):
    def __init__(self, val, nxt=None):
        self.val = val
        self.next = nxt

class Cell(object):
    def __init__(self, val, nxt=None):
        self.val = val
        self.next = nxt

    def copy(self):
        if self.next is None:
            return Cell(self.value)
        else:
            return Cell(self.value, self.next.copy())

