def show_tgz():
    count = 1
    indexMapping = {}
    for i, fname in enumerate(dirlist):
        if fname.endswith(('.tgz','.tar')):
            print '\n{0:3d} - {1}'.format(count, fname)
            indexMapping[count] = i
            count += 1
    return indexMapping

