def get_table(proj, max_x, max_y):
   table = [[False for x in xrange(max_x)] for y in xrange(max_y)]
   tasks = Task.objects.filter(proj=proj)
   for task in tasks:
      table[task.posy-1][task.posx-1] = True
   return table

