#Reflect each database table we need to use, using metadata class Customer(Base): __table__ = Table('Customers', metadata, autoload=True) orders = relationship("Order", backref="customer")
class Shipper(Base): __table__ = Table('Shippers', metadata, autoload=True) orders = relationship("Order", backref="shipper")
