class Territory(Base): __table__ = Table('Territories', metadata, autoload=True) region = relationship('Region', backref='territories')
class Region(Base): __table__ = Table('Region', metadata, autoload=True)
class Order(Base): __table__ = Table('Orders', metadata, autoload=True) products = relationship('Product', secondary=Table('Order Details', metadata, autoload=True)) employee = relationship('Employee', backref='orders')
class Product(Base): __table__ = Table('Products', metadata, autoload=True) supplier = relationship('Supplier', backref='products') category = relationship('Category', backref='products')
class Supplier(Base): __table__ = Table('Suppliers', metadata, autoload=True)
class Category(Base): __table__ = Table('Categories', metadata, autoload=True)
