from graphics import GraphWin, Point, Line, Rectangle, Text
def draw_window() # create canvas win = GraphWin("Regression Line - Start Clicking!", 500, 500) win.setCoords(0., 0., 10., 10.) # exit button rect = Rectangle(Point(0.5, 0.1), Point(2.5, 2.1)) rect.setFill("red") rect.draw(win) Text(rect.getCenter(), "Done").draw(win) # instructions Text(Point(5., 0.5), "Click in this screen").draw(win) return win
def get_points(win): points = [] while True: p = win.getMouse() p.draw(win) # clicked the exit button? px, py = p.getX(), p.getY() if 0.5 <= px <= 2.5 and 0.1 <= py <= 2.1: break else: points.append((px,py)) return points
def do_regression(points): num = len(points) x_sum, y_sum, xx_sum, xy_sum = 0., 0., 0., 0. for x,y in points: x_sum += x y_sum += y xx_sum += x*x xy_sum += x*y x_mean, y_mean = x_sum/num, y_sum/num m = (xy_sum - num*x_mean*y_mean) / (xx_sum - num*x_mean*x_mean) def lineFn(xval): return y_mean + m*(xval - x_mean) return lineFn
def main(): # set up win = draw_window() points = get_points(win) # show regression line lineFn = do_regression(points) Line( Point(0., lineFn(0. )), Point(10., lineFn(10.)) ).draw(win) # wait to close Text(Point(5., 5.), "Click to exit").draw(win) win.getMouse() win.close()
if __name__=="__main__": main()

