class HeadlessBrowser(object):
def __init__(self, instance_id): self.webkit_wrapper = WebkitWrapper() self.instance_id = instance_id
def _return_intance_id(self, result): return self.instance_id, result
def _request_failed(self, failure): failure.trap(Exception) err_msg = failure.getErrorMessage() print err_msg failure.printDetailedTraceback() return err_msg
def shutdown(self): self.webkit_wrapper.shutdown()
def _run_perform(self, _r, request_data): return self.webkit_wrapper.perform(request_data)
def get_request(self, request_data): deferred_request = self.webkit_wrapper.perform(request_data) deferred_request.addErrback(self._request_failed) deferred_request.addBoth(self._return_intance_id) return deferred_request
