class Dec(object):
  def __init__(self, fn):
    self._fn = fn
    def setbefore(b):
        self._before = b
    self._before = self.default_before = setbefore

def __call__(self, *args, **kwargs):
    if self._before != self.default_before:
       self._before(*args, **kwargs)
    self._fn(*args, **kwargs)

