from xml.dom.minidom import Document import copy import re from collections import OrderedDict
class d2x: ''' Converts Dictionary to XML ''' def __init__(self, structure): self.doc = Document() if len(structure) == 1: rootName = str(structure.keys()[0]) self.root = self.doc.createElement(rootName) self.doc.appendChild(self.root) self.build(self.root, structure[rootName])
def build(self, father, structure): if type(structure) == dict: for k in structure: tag = self.doc.createElement(k) father.appendChild(tag) self.build(tag, structure[k]) elif type(structure) == OrderedDict: for k in structure: tag = self.doc.createElement(k) father.appendChild(tag) self.build(tag, structure[k])
elif type(structure) == list: grandFather = father.parentNode tagName = father.tagName grandFather.removeChild(father) for l in structure: tag = self.doc.createElement(tagName) self.build(tag, l) grandFather.appendChild(tag)
else: data = str(structure) tag = self.doc.createTextNode(data) father.appendChild(tag)
def display(self): # I render from the root instead of doc to get rid of the XML header #return self.root.toprettyxml(indent=" ") return self.root.toxml()
class SolaceNode: ''' a sub dictionary builder ''' def __init__(self): self.__dict__ = OrderedDict() def __getattr__(self, name): name = re.sub("_", "-", name) try: return self.__dict__[name] except: self.__dict__[name] = SolaceNode() return self.__dict__[name] def __str__(self): return str(self.__dict__) def __repr__(self): return str(self.__dict__) def __call__(self, *args, **kwargs): return self.__dict__ def __setattr__(self, name, value): name = re.sub("_", "-", name) self.__dict__[name] = value
class SolaceXMLBuilder(object): ''' main dictionary builder
Any dot-name-space like calling of a instance of SolaceXMLBuilder will create nested dictionary keys. These are converted to XML whenever the instance representation is called ( __repr__ )
Example
a=SolaceXMLBuilder() a.foo.bar.baz=2 str(a) '<rpc semp-version="soltr/5_5">\n<foo><bar><baz>2</baz></bar></foo></rpc>'
''' def __init__(self): self.__dict__ = OrderedDict() self.__setattr__ = None def __getattr__(self, name): name = re.sub("_", "-", name) try: return self.__dict__[name] except: self.__dict__[name] = SolaceNode() return self.__dict__[name] def __repr__(self): # Show XML myxml = d2x(eval(str(self.__dict__))) # I had to conjur up my own header cause solace doesnt like </rpc> to have attribs #return str('<rpc semp-version="soltr/5_5">\n%s</rpc>' % myxml.display()) return str('<rpc semp-version="soltr/5_5">\n%s</rpc>' % myxml.display()) def __call__(self, *args, **kwargs): return self.__dict__ # def __setattr__(self, name, value): # raise Exception('no you cant create assignments here, only on sub-elements')
