def merge(left, right, aux=[]):
    while left and right:
        if left[0] > right[0]:
            aux.append(right.pop(0))
        else:
            aux.append(left.pop(0))
    aux.extend(right)
    aux.extend(left)
    return aux

class MyClass(object):
    def __init__(self):
        self.data = {'a': 'v1', 'b': 'v2'}
    def __getattr__(self, attr):
        return self.data[attr]

class MyClass(object):
    def __init__(self):
        ''' prevents infinite recursion by calling base class '''
        super(MyClass, self).__setattr__('data', {'a': 'v1', 'b': 'v2'})
    def __setattr__(self, k, v):
        self.data[k] = v
    def __getattr__(self, k):
        ''' we don't need a special call to super here because getattr is only 
            called when an attribute is not found in the instance's dictionary'''
        return self.data[k]

class MyClass(namedtuple("MyClass", ["a", "b"])):
    def __new__(cls, a="v1", b="v2"):
        return super(MyClass, cls).__new__(cls, a, b)

