def depchange(change):
    depchange_status=''
    if change == "23456":
        depchange="33456"
        depchange_status = 'NEW'
    if change == "33456":
        depchange=""
        depchange_status = 'COMPLETED'
    return (depchange,depchange_status)


def main ():
    master_change="23456"
    dep={}
    while True:
        dep_change,depchange_status=depchange(master_change)
        dep[master_change]=[dep_change]
        master_change = dep_change
        if depchange_status != 'NEW':
            break
    print dep

if __name__ == '__main__':
    main()

def lookup(x, y, l):
    for xx in xrange(x-l, x+l+1):
        for yy in xrange(y-l, y+l+1):
            yield (xx, yy)
list(lookup(50, 50, 10))

#include "lib2.h"
extern "C" {
__declspec(dllexport) lib2* lib2_new() { return new lib2; }
__declspec(dllexport) void lib2_delete(lib2* p) { delete p; }
__declspec(dllexport) void lib2_func(lib2* p, char* c1, char* c2, double d) {
    p->func(c1,c2,d);
}
}

#ifdef LIB2_EXPORTS
#   define LIB2_API __declspec(dllexport)
#else
#   define LIB2_API __declspec(dllimport)
#endif

class LIB2_API lib2
{
public:
    void func(char * c1, char * c2, double d);
};

#include <stdio.h>
#include "lib2.h"

void lib2::func(char * c1, char * c2, double d)
{
    printf("%s %s %f\n",c1,c2,d);
}

#!python3 from ctypes import *
class lib2:
lib1 = CDLL('lib1') # It's best to declare all arguments and types, so Python can typecheck. lib1.lib2_new.argtypes = [] lib1.lib2_new.restype = c_void_p # Can use this for an opaque pointer. lib1.lib2_func.argtypes = [c_void_p,c_char_p,c_char_p,c_double] lib1.lib2_func.restype = None lib1.lib2_delete.argtypes = [c_void_p] lib1.lib2_delete.restype = None
def __init__(self): self.obj = self.lib1.lib2_new()
def __del__(self): self.lib1.lib2_delete(self.obj)
def func(self,c1,c2,d): self.lib1.lib2_func(self.obj,c1,c2,d)
o = lib2() o.func(b'abc',b'123',1.2) # Note byte strings

