 #
 # zip_test: 0.0131301879883
 # ast_test: 0.0835828781128

class Vector2D:
    def __init__(self, x, y, vx, vy):
        self.x = x
        self.y = y
        self.vx = vx
        self.vy = vy

    def update(self):
        self.x += self.vx
        self.y += self.vy

    def copyto(self, rect):
        rect.x = int(round(self.x,0)) 
        rect.y = int(round(self.y,0))


speed = Vector2D(100, 100, .5, .5)

