def byte_array(n):
    for i in range(2**(8*n)):
        yield [[i>>k&1 for k in range(j, j-8, -1)] for j in range(8*n-1, 0, -8)]

def byte_array(n):
    for i in range(2**(8*n)):
        yield [i>>j&1 for j in range(8*n-1, -1, -1)]

acc = None
for idx, ch in enumerate(input):
    if ch in '1234567890':
        if acc is None: acc = 0
        acc = 10 * acc + int(ch)
        continue
    if acc != None:
        yield acc
        acc = None
    if ch in '+-=x':
        yield ch
    elif ch == ' ':
        pass
    else:
        raise ValueError('illegal character "%s" at %d' % (ch, idx))

