from django.template import Context, Template
def getdjtemplate(th_span="1"):
    dj_template ="""
    <table class="out_">
    {# headings #}
        <tr>
        {% for heading in headings %}
            <th colspan={{ th_span }}>{{ heading }}</th>
        {% endfor %}
        </tr>
    </table>
    """
    t = Template(dj_template)
    headings = ["Hello"]
    c = Context({'headings':headings, 'th_span':th_span})
    return t.render(c)

