def doublifier(iterable):
    it = iter(iterable)
    while True:
        line = next(it)
        if line.startswith('#'):
            yield line, next(it)
        else:
            yield (line,)

def isvalid(*values):
    for value in values:
        #check a value is invalid 
        if not value in ['pound', 'euro', 'dollar', 'yen']: #etc...
            return False
    return True

