from inspect import stack

def where():
    caller_frame = stack()[1]
    return caller_frame[0].f_globals.get('__file__', None)

def where():
    caller_frame = stack()[1]
    return caller_frame[1]

def grouper(sequence):
    group, members = [], set()

    for item in sequence:
        if group and members.isdisjoint(item):
            # new group, yield and start new
            yield group
            group, members = [], set()
        group.append(item)
        members.update(item)

    yield group

def grouper(sequence): result = [] # will hold (members, group) tuples
for item in sequence: for members, group in result: if members.intersection(item): # overlap members.update(item) group.append(item) break else: # no group found, add new result.append((set(item), [item]))
return [group for members, group in result]

