import collections

def flatten(collection):
  for element in collection:
    if isinstance(element, collections.Iterable) and not isinstance(element, str):
      for x in flatten(element):
        yield x
    else:
      yield element

L = [1,2,[3,4,(5,6,7,{'abc':1,'xyz':2}),8,9],10]

