def PrettyPrintCitation(row) :
    def adjustauthors(s):
        authorlist = s[0].split(';')
        if(len(authorlist)<2) :
            s[0] = authorlist[0]
        elif(len(authorlist)==2) :
            s[0] = '{0} & {1}'.format(*authorlist)
        else :
            s[0] = ', '.join(authorlist[:-1]) + ', & ' + authorlist[-1]
        return s

    print('{0}. ({1}). {2}. {3}.'.format(*adjustauthors(row)))

