def concat(a, b, c): return a + b + c
def concat_from_list(lst): return lst[0] + lst[1] + lst[2]
def join(a, b, c): return "".join([a, b, c])
def join_tuple(a, b, c): return "".join((a, b, c))
def join_from_list(lst): return "".join(lst)
