class __VecRot:

    def __init__(self, coords):
        self.coords = coords

    def double(self):
        self.coords = [i*2 for i in self.coords]

    def __len__(self):
        return len(self.coords)

    def __str__(self):
        return "__VecRot["+str(self.coords)+"]"

    def __repr__(self):
        return "__VecRot("+repr(self.coords)+")"

a = __VecRot([1,0,0])

a.double()

print a

