def i64str(obj):
    if isinstance(obj, list):
        return [i64str(x) for x in obj]
    if isinstance(obj, dict):
        return {k:i64str(v) for k, v in obj.items()}
    if isinstance(obj, (int, long)) and obj >= 2**32:
        return str(obj)
    return obj

