int fib(int n){
    int i = 2;    /* Obviously 1 can divide all and 2 is the base of primality*/
    while (i < n) {
        if (n % i == 0){
            return 0;
            break;
        }
        else {
            i = i + 1;
            if (i == (n-1)){ return 1; }
        }
    }
    return 0;
}

