nodes = []

def process_subtree(c, path):
    name = c.get('Name') if c.tag == 'Node' else None
    if name:
        path.append(name)
        nodes.append('/'.join(path))

    for c1 in c:
        process_subtree(c1, path)

    if name:
        path.pop()

