def posNumAvg(values):
    x = []
    average = 0

    for i in values:
        if i > 0:
            x.append(i)
    average = sum(x) / len(x)

    return average

def posNumAvg(values):
    pos = [v for v in values if v > 0]
    return sum(pos) / len(pos)

def negativeNumAvg(values):
    x = []
    average = 0

    for i in values:
        if i < 0:
            x.append(i)
    average = sum(x) / len(x)

    return average

