import binascii

def parse_bgcolor(bgcolor):
    if not bgcolor.startswith('#'):
        raise ValueError('A bgcolor must start with a "#"')
    return binascii.unhexlify(bgcolor[1:])

def is_bgcolor(bgcolor):
    try:
        parse_bgcolor(bgcolor)
    except Exception as e:
        return False
    else:
        return True

from collections import defaultdict
def find_controlled(company, shares): # shares the company owns directly controls = set([company]) # avoid the recursion problem pointed out by @Justin owns = defaultdict(float) owns.update(shares[company])
# first level of controlled companies to look at add_control = set(k for k,v in owns.items() if v > 0.50)
while add_control: # new level of controlled companies to look at next _add_control = set()
for cpy in add_control: controls.add(cpy) for k,v in shares[cpy].items(): owns[k] += v if owns[k] > 0.5 and k not in controls: _add_control.add(k) add_control = _add_control
return sorted(controls - set([company]))
