def _sub_ip(self, line): pattern = r"((25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)([ (\[]?(\.|dot)[ )\]]?(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)){3})" ips = [each[0] for each in re.findall(pattern, line)] for item in ips: location = ips.index(item) ip = re.sub("[ ()\[\]]", "", item) ip = re.sub("dot", ".", ip) ips.remove(item) ips.insert(location, ip)
for ip in ips: line = line.replace(ip, self._ip2db(ip))
return line

